class Solution:

    def characterReplacement(self, s: str, k: int) -> int:
        print('len',len(s))
        max_result = 0

        char_list_key = {}

        for i in range(0, len(s)):

            if s[i] not in char_list_key:

                char_list_key[s[i]] = [i]

            else:

                char_list_key[s[i]].append(i)

        print(char_list_key)

        max_result = 0

        for char_idx in list(char_list_key):
            #print(char_idx)
            temp_char_list = char_list_key[char_idx]
            print(len(temp_char_list))
            start_idx = 0

            end_idx = 0

            current_max = 0

            current_k = min(k, len(s) - len(temp_char_list))

            while True:
                if end_idx == len(temp_char_list) - 1:

                    max_diff = temp_char_list[end_idx] - temp_char_list[start_idx] + 1

                    rest_left = start_idx

                    rest_right = len(s) - 1 - (end_idx + 1) + 1

                    if rest_right < 0:
                        rest_right = 0

                    if current_k > 0:

                        rest = rest_left + rest_right

                        if rest <= current_k:
                            max_diff += rest
                        else:
                            max_diff += current_k

                    current_max = max(current_max,max_diff)

                    break

                else:

                    diff = temp_char_list[end_idx + 1] - temp_char_list[end_idx] - 1

                    if diff > k:

                        rest_left = start_idx

                        rest_right = len(s) - 1 - (end_idx + 1) + 1

                        if rest_right < 0:
                            rest_right = 0

                        rest =  rest_left +rest_right

                        if rest <= current_k:

                            current_max = max(current_max, temp_char_list[end_idx] - temp_char_list[start_idx] + 1+rest)
                        else:

                            current_max = max(current_max, temp_char_list[end_idx] - temp_char_list[start_idx] + 1 +current_k)


                        start_idx = end_idx + 1
                        end_idx = end_idx + 1

                        current_k = min(k, len(s) - len(temp_char_list))

                        #print('case 1 max', current_max)

                    elif diff <= current_k:

                        current_k -= (diff)

                        end_idx = end_idx + 1
                        current_max = max(current_max,  temp_char_list[end_idx] - temp_char_list[start_idx] + 1)

                    else:

                        current_k += (temp_char_list[start_idx + 1] - temp_char_list[start_idx] - 1)

                        start_idx += 1
                        #current_max = max(current_max, temp_char_list[end_idx] - temp_char_list[start_idx] + 1)
                        #print('case 1 max', current_max)

            max_result = max(max_result, current_max)
        print('=====')
        return max_result

